import React, { Component } from "react";

class Square extends Component {
	constructor(props) {
		super(props);
		this.increase = this.increase.bind(this); // constructor is used for binding 'this' only! props are automatically passed through
	}

	increase(e) {
		this.setState({
			count: this.props.count + 1
		})
	}

  render() {
  	var squareStyle = {
  		height: 250,
  		width: 250,
  		padding: 0,
  		backgroundColor: 'red'
  	}
    return (
    	<div style={squareStyle}>
    		<h1>{this.props.count}</h1>
    		<button onClick={this.increase}>Increase Count +</button>
    	</div>
    );
  }
}

export default Square	;
