import React, { Component } from "react";

class Square extends Component {
	constructor(props) {
		super(props);

		this.state = {
			// count: 0
		};

		this.increase = this.increase.bind(this);
	}

	increase(e) {
		this.setState({
			count: this.state.count + 1
		})
	}


	// ALL LIFECYCLE METHOD EXAMPLES:
	//===== ON INITIAL RENDER =====
	// 1. defaultProps : (called outside scope of component.) : ex. Square.defaultProps = {count: 1}
	componentWillMount() {

	}
	componentDidMount() {

	}






  render() {
  	var squareStyle = {
  		height: 250,
  		width: 250,
  		padding: 0,
  		backgroundColor: 'red'
  	}
    return (
    	<div style={squareStyle}>
    		<p>{this.props.cardClickCount}</p>
    		<h1>{this.state.count}</h1>
    		<button onClick={this.increase}>Increase Count +</button>
    	</div>
    );
  }
}

Square.defaultProps = {count: 1}
export default Square	;
